const content = $json["message"]["content"];

// Regex para capturar qualquer URL que apareça no texto
const regex = /(https?:\/\/[^\s)]+)/g;
const matches = content.match(regex);

// Se encontrou alguma URL, processa
if (matches && matches.length > 0) {
  const url = matches[0];

  // Pega a última parte da URL (o arquivo)
  const filenameWithExt = url.split("/").pop();

  // Remove a extensão do arquivo (ex: .jpg, .png, .webp, etc.)
  const filename = filenameWithExt.replace(/\.[^/.]+$/, "");

  return [
    {
      json: {
        imageUrl: url,
        fileName: filename,
      },
    },
  ];
} else {
  // Caso nenhuma URL seja encontrada
  return [
    {
      json: {
        imageUrl: null,
        fileName: null,
      },
    },
  ];
}

