let projects = [];

// 1) Tenta ler como objeto JSON da entrada
try {
  const output = $input.first()?.json?.output;

  if (typeof output === "string" && output.trim().length > 0) {
    const parsed = JSON.parse(output);
    if (parsed.best_projects && Array.isArray(parsed.best_projects)) {
      projects = parsed.best_projects.map(p => ({
        id: String(p.id ?? ""),
        title: String(p.title ?? ""),
        description: String(p.description ?? ""),
        complexity: String(p.complexity ?? ""),
        your_analyze: String(p.your_analyze ?? ""),
        why_fit: Array.isArray(p.why_fit) ? p.why_fit : [],
        scope_outline: Array.isArray(p.scope_outline) ? p.scope_outline : [],
        delivery_time_estimate: String(p.delivery_time_estimate ?? ""),
        price_range: String(p.price_range ?? ""),
        risks: Array.isArray(p.risks) ? p.risks : [],
        recommendations: Array.isArray(p.recommendations) ? p.recommendations : [],
        meta: {
          project_link: String(p.meta?.project_link ?? ""),
          budget_input: String(p.meta?.budget_input ?? ""),
          proposals: Number(p.meta?.proposals ?? 0),
          interested: Number(p.meta?.interested ?? 0),
          posted_days_ago: Number(p.meta?.posted_days_ago ?? 0)
        }
      }));
    }
  }
} catch (e) {
  // ignore
}

// 2) Retornar apenas os objetos dos projetos
const result = [];
for (let i = 0; i < 5; i++) {
  if (projects[i]) {
    result.push(projects[i]);
  } else {
    result.push({
      id: "",
      title: "",
      description: "",
      complexity: "",
      your_analyze: "",
      why_fit: [],
      scope_outline: [],
      delivery_time_estimate: "",
      price_range: "",
      risks: [],
      recommendations: [],
      meta: {
        project_link: "",
        budget_input: "",
        proposals: 0,
        interested: 0,
        posted_days_ago: 0
      }
    });
  }
}

// Output
return result;
