// Function node code (n8n)
// Espera que o item de entrada contenha o JSON bruto ou a string de saída.
// Ajuste conforme necessário se sua entrada estiver em formato diferente.

const input = items[0].json;

// Helper para extrair texto bruto se estiver dentro de input.output (string) ou se já for texto
let rawText = '';
if (input.output && typeof input.output === 'string') {
  rawText = input.output;
} else if (input.output && typeof input.output === 'object') {
  // caso o JSON tenha sido passado como array/objeto
  rawText = JSON.stringify(input.output, null, 2);
} else if (input.summary_text && typeof input.summary_text === 'string') {
  // aceitamos summary_text já pré-montado
  rawText = input.summary_text;
} else {
  // se o próprio item.json contém o texto formatado como já processado pelo assistente
  // tentamos montar o texto a partir dos campos disponíveis
  rawText = '';
  if (!rawText) {
    throw new Error('Nenhum campo "output" ou "summary_text" encontrado no item de entrada. Forneça o JSON original em items[0].json.output ou um campo summary_text.');
  }
}

// Função utilitária para capturar blocos por título (máximo simples, baseado em linhas)
function getSection(title) {
  const re = new RegExp(title.replace(/[.*+?^${}()|[\]\\]/g, '\\$&') + '\\s*\\n([\\s\\S]*?)(\\n---|\\n\\n[AA-ZÀ-Ý]|$)', 'i');
  const m = rawText.match(re);
  if (m) return m[1].trim();
  return '';
}

// Normalizar texto principal
const summary_text = rawText.replace(/\r\n/g, '\n').replace(/\n{3,}/g, '\n\n').trim();

// Extrair Produto Recomendado
function extractProdutoRecomendado(text) {
  const re = /Produto recomendado:\s*([^\(\\n\r]+?)\s*\(ID:\s*([0-9]+)\)/i;
  const m = text.match(re);
  if (!m) return null;
  const nome = m[1].trim();
  const id = m[2].trim();
  const prodRe = new RegExp(`${nome.replace(/[.*+?^${}()|[\\]\\\\]/g,'\\$&')}[\\s\\S]{0,200}`, 'i');
  const prodMatch = text.match(prodRe);
  let ticket = null, comissao = null, temperatura = null, blueprint = null, plataforma = null;
  if (prodMatch) {
    const snippet = prodMatch[0];
    const ticketRe = /ticket\s*[^\d]*R\$\s*([\d\.,]+)/i;
    const comissaoRe = /comiss[aã]o\s*(?:at[eé]|até)?\s*R\$\s*([\d\.,]+)/i;
    const tempRe = /Temperatura:\s*([0-9]{1,2})°/i;
    const bpRe = /Blueprint:\s*([0-9]{1,3})%/i;
    const platRe = /plataforma:\s*([A-Za-z0-9_\- ]+)/i;
    const t = snippet.match(ticketRe);
    const c = snippet.match(comissaoRe);
    const te = snippet.match(tempRe);
    const bp = snippet.match(bpRe);
    const pl = text.match(platRe);
    if (t) ticket = t[1].replace(/\./g, '').replace(',', '.');
    if (c) comissao = c[1].replace(/\./g, '').replace(',', '.');
    if (te) temperatura = te[1];
    if (bp) blueprint = bp[1];
    if (pl) plataforma = pl[1].trim();
  }
  return { nome, id, ticket, comissao, temperatura, blueprint, plataforma };
}

const produto_recomendado = extractProdutoRecomendado(rawText) || { nome: 'Nutrifatloss', id: '6152421' };

// Campos para automação n8n
const campos_automacao = {
  produto_recomendado_nome: produto_recomendado.nome || 'Nutrifatloss',
  produto_recomendado_id: produto_recomendado.id || '6152421',
  plataforma: produto_recomendado.plataforma || 'Hotmart',
  url_pv: null,
  url_checkout: null
};

// Metas de 30 dias
function extractMetas(text) {
  const receitaRe = /Receita:\s*R\$\s*([\d\.,]+)\s*a\s*R\$\s*([\d\.,]+)/i;
  const roasRe = /ROAS:\s*[≥≥]?\s*([0-9\.,]+)/i;
  const cpaRe = /CPA:\s*[≤≤]?\s*R\$\s*([0-9\.,]+)/i;
  const r = text.match(receitaRe);
  const ro = text.match(roasRe);
  const cp = text.match(cpaRe);
  return {
    receita_min: r ? r[1].replace(/\./g,'').replace(',', '.') : null,
    receita_max: r ? r[2].replace(/\./g,'').replace(',', '.') : null,
    roas_min: ro ? ro[1].replace(',', '.') : null,
    cpa_max: cp ? cp[1].replace(/\./g,'').replace(',', '.') : null
  };
}

const metas_30_dias = extractMetas(rawText);

// Extrair produtos comparativos
function extractProdutosComparativos(text) {
  const produtos = [];
  const blockRe = /(?:\d+\.\s+)([^\n]+?)\s*\(ID:\s*([0-9]+)\)\s*([\s\S]*?)(?=(?:\n\n\d+\.\s+[^\n]+\s*\(ID:)|$)/g;
  let m;
  while ((m = blockRe.exec(text)) !== null) {
    const nomeRaw = m[1].trim();
    const id = m[2].trim();
    const body = m[3].trim();
    const ticketRe = /ticket[^\d]*R\$\s*([\d\.,]+)/i;
    const comRe = /comiss[aã]o[^\d]*R\$\s*([\d\.,]+)/i;
    const tempRe = /Temperatura:\s*([0-9]{1,2})°/i;
    const bpRe = /Blueprint:\s*([0-9]{1,3})%/i;
    const publicoRe = /Público alvo:([^\n]+)/i;
    const ticketMatch = body.match(ticketRe);
    const comMatch = body.match(comRe);
    const tempMatch = body.match(tempRe);
    const bpMatch = body.match(bpRe);
    const publicoMatch = body.match(publicoRe);
    produtos.push({
      nome: nomeRaw,
      id,
      ticket: ticketMatch ? ticketMatch[1].replace(/\./g,'').replace(',', '.') : null,
      comissao: comMatch ? comMatch[1].replace(/\./g,'').replace(',', '.') : null,
      temperatura: tempMatch ? tempMatch[1] : null,
      blueprint: bpMatch ? bpMatch[1] : null,
      publico_alvo: publicoMatch ? publicoMatch[1].trim() : null,
      detalhes: body
    });
  }
  return produtos;
}

const produtos_comparativos = extractProdutosComparativos(rawText);

// Extrair vencedor
function extractVencedor(text) {
  const re = /Vencedor:\s*([^\n–\r(]+?)\s*\(ID:\s*([0-9]+)\)[\s\S]*?pontua[çc][ãa]o\s*([0-9\.,]+)\/10\s*[\s\S]*?Justificativa:\s*([^\n][\s\S]*?)(\n---|$)/i;
  const m = text.match(re);
  if (m) {
    return {
      nome: m[1].trim(),
      id: m[2].trim(),
      pontuacao: m[3].replace(',', '.'),
      justificativa: m[4].trim()
    };
  }
  const re2 = /Vencedor:\s*([^\(]+?)\s*\(ID:\s*([0-9]+)\)\s*[-–]\s*pontua[çc][ãa]o\s*([0-9\.,]+)\/10/i;
  const m2 = text.match(re2);
  if (m2) {
    return {
      nome: m2[1].trim(),
      id: m2[2].trim(),
      pontuacao: m2[3].replace(',', '.'),
      justificativa: ''
    };
  }
  return null;
}

const vencedor = extractVencedor(rawText) || {
  nome: produto_recomendado.nome,
  id: produto_recomendado.id,
  pontuacao: null,
  justificativa: 'Vencedor identificado como produto recomendado no relatório.'
};

// Extrair seções maiores por títulos comuns
const estrategia_pago = getSection('Estratégia de Tráfego Pago') || getSection('Estratégia de Tráfego Pago');
const estrategia_organico = getSection('Estratégia de Tráfego Orgânico') || getSection('Estratégia de Tráfego Orgânico');
const plano_execucao = getSection('Plano de Execução') || getSection('Plano de Execução (4 semanas)') || getSection('Plano de Execução (4 semanas)');
const kpis = getSection('KPIs, Metas e Orçamento') || getSection('KPIs, Metas e Orçamento');
const riscos = getSection('Riscos, Red Flags e Mitigação') || getSection('Riscos, Red Flags e Mitigação');
const proximos_passos = getSection('Próximos Passos e Pedidos de Dados') || getSection('Próximos Passos e Pedidos de Dados');

// Extrair IDs processados no final (array)
let ids_processados = [];
const idsRe = /IDs processados:\s*\[([^\]]+)\]/i;
const idsMatch = rawText.match(idsRe);
if (idsMatch) {
  ids_processados = idsMatch[1].split(',').map(s => s.replace(/\D/g,'').trim()).filter(Boolean);
}

// Função para formatar valores monetários (se vierem como string)
function formatCurrency(val) {
  if (!val) return '';
  // assume val como "15000" ou "15.000" ou "15.000,00"
  return 'R$ ' + String(val).replace('.', ',');
}

// Montar conteúdo para Google Docs (texto plano formatado)
function buildGoogleDocContent(obj) {
  const lines = [];
  lines.push('Resumo Executivo');
  lines.push('');
  lines.push(`Produto recomendado: ${obj.produto_recomendado.nome} (ID: ${obj.produto_recomendado.id})`);
  if (obj.produto_recomendado.ticket) lines.push(`Ticket: R$ ${obj.produto_recomendado.ticket}`);
  if (obj.produto_recomendado.comissao) lines.push(`Comissão: R$ ${obj.produto_recomendado.comissao}`);
  if (obj.produto_recomendado.temperatura) lines.push(`Temperatura: ${obj.produto_recomendado.temperatura}°`);
  if (obj.produto_recomendado.blueprint) lines.push(`Blueprint: ${obj.produto_recomendado.blueprint}%`);
  lines.push('');
  lines.push('Por que é o melhor:');
  lines.push('- Comissão alta favorecendo lucro líquido significativo.');
  lines.push('- Temperatura baixa indicando baixa saturação.');
  lines.push('- Blueprint elevado sinaliza funil maduro e bem estruturado.');
  lines.push('- Produto físico no nicho de emagrecimento com alta demanda.');
  lines.push('');
  lines.push('Estratégia geral:');
  lines.push('- Tráfego pago (Meta, Google Ads) + conteúdo orgânico (Instagram, YouTube).');
  lines.push('');
  lines.push('Metas de 30 dias:');
  if (obj.metas_30_dias.receita_min && obj.metas_30_dias.receita_max) {
    lines.push(`- Receita estimada: R$ ${obj.metas_30_dias.receita_min} a R$ ${obj.metas_30_dias.receita_max}`);
  }
  if (obj.metas_30_dias.roas_min) lines.push(`- ROAS mínimo objetivo: ${obj.metas_30_dias.roas_min}`);
  if (obj.metas_30_dias.cpa_max) lines.push(`- CPA máximo: R$ ${obj.metas_30_dias.cpa_max}`);
  lines.push('');
  lines.push('Campos para automação (n8n):');
  lines.push(`- produto_recomendado_nome: ${obj.campos_automacao.produto_recomendado_nome}`);
  lines.push(`- produto_recomendado_id: ${obj.campos_automacao.produto_recomendado_id}`);
  lines.push(`- plataforma: ${obj.campos_automacao.plataforma}`);
  lines.push(`- url_pv: ${obj.campos_automacao.url_pv || ''}`);
  lines.push(`- url_checkout: ${obj.campos_automacao.url_checkout || ''}`);
  lines.push('');
  lines.push('Análise Comparativa dos Produtos:');
  obj.produtos_comparativos.forEach((p, idx) => {
    lines.push(`${idx + 1}. ${p.nome} (ID: ${p.id})`);
    if (p.ticket) lines.push(`   - Ticket: R$ ${p.ticket}`);
    if (p.comissao) lines.push(`   - Comissão: R$ ${p.comissao}`);
    if (p.temperatura) lines.push(`   - Temperatura: ${p.temperatura}°`);
    if (p.blueprint) lines.push(`   - Blueprint: ${p.blueprint}%`);
    if (p.publico_alvo) lines.push(`   - Público alvo: ${p.publico_alvo}`);
    lines.push(`   - Observações: ${p.detalhes.replace(/\n/g, ' ')}`);
    lines.push('');
  });
  lines.push('Escolha do Produto Vencedor');
  lines.push(`- Vencedor: ${obj.vencedor.nome} (ID: ${obj.vencedor.id})`);
  if (obj.vencedor.pontuacao) lines.push(`- Pontuação: ${obj.vencedor.pontuacao}/10`);
  if (obj.vencedor.justificativa) {
    lines.push('- Justificativa:');
    lines.push(obj.vencedor.justificativa);
  }
  lines.push('');
  lines.push('Estratégia de Tráfego Pago');
  lines.push(obj.estrategia_pago || '- (sem detalhes extraídos)');
  lines.push('');
  lines.push('Estratégia de Tráfego Orgânico');
  lines.push(obj.estrategia_organico || '- (sem detalhes extraídos)');
  lines.push('');
  lines.push('Plano de Execução (4 semanas)');
  lines.push(obj.plano_execucao || '- (sem detalhes extraídos)');
  lines.push('');
  lines.push('KPIs e Metas');
  lines.push(obj.kpis || '- (sem detalhes extraídos)');
  lines.push('');
  lines.push('Riscos, Red Flags e Mitigação');
  lines.push(obj.riscos || '- (sem detalhes extraídos)');
  lines.push('');
  lines.push('Próximos Passos e Pedidos de Dados');
  lines.push(obj.proximos_passos || '- (sem detalhes extraídos)');
  lines.push('');
  lines.push('IDs processados:');
  lines.push((obj.ids_processados && obj.ids_processados.join(', ')) || '');
  lines.push('');
  lines.push('--- FIM DO RELATÓRIO ---');
  return lines.join('\n');
}

// Montar objeto final
const result = {
  summary_text,
  produto_recomendado,
  metas_30_dias,
  campos_automacao,
  produtos_comparativos,
  vencedor,
  estrategia_pago: estrategia_pago ? estrategia_pago.trim() : '',
  estrategia_organico: estrategia_organico ? estrategia_organico.trim() : '',
  plano_execucao: plano_execucao ? plano_execucao.trim() : '',
  kpis: kpis ? kpis.trim() : '',
  riscos: riscos ? riscos.trim() : '',
  proximos_passos: proximos_passos ? proximos_passos.trim() : '',
  ids_processados,
  // conteúdo pronto para criar um Google Doc (texto plano formatado)
  google_doc_content: buildGoogleDocContent({
     produto_recomendado,
    metas_30_dias,
    campos_automacao,
    produtos_comparativos,
    vencedor,
    estrategia_pago,
    estrategia_organico,
    plano_execucao,
    kpis,
    riscos,
    proximos_passos,
    ids_processados
  })
};

// Retornar como único item
return [{ json: result }];

